import React from 'react';
import {Collection} from './Collection'
import './index.scss';

const categories = [];
function App() {
  const [searchValue, setSearchValue] = React.useState('');
  const [category, setCategory] = React.useState(0);
  const [collections, setCollections] = React.useState([]);

  React.useEffect(() => {
    fetch('https://6442a81e76540ce225923e4b.mockapi.io/collections')
      .then((res) => (res.json()))
      .then((json) => {
        setCollections(json);
      })
      .catch((error) => {
        console.warn(error);
        alert("Error, data can't be retrieved...");
      })
  }, []);
  
  return (
    <div className="App">
      <h1>Моя коллекция фотографий</h1>
      <div className="top">
        <ul className="tags">
          <li className="active">Все</li>
          <li>Горы</li>
          <li>Море</li>
          <li>Архитектура</li>
          <li>Города</li>
        </ul>
        <input className="search-input" placeholder="Поиск по названию" value={searchValue} onChange={(event) => setSearchValue(event.target.value)}/>
      </div>
      <div className="content">
        {
          collections.filter((collection) => {return collection.name.toLowerCase().includes(searchValue.toLowerCase())}).map((collection, index) => (
            <Collection key={index}
            name={collection.name}
            images={collection.photos}
          />    
          ))
        }
      </div>
      <ul className="pagination">
        <li>1</li>
        <li className="active">2</li>
        <li>3</li>
      </ul>
    </div>
  );
}

export default App;
